<template>

<div>
  <img
      @click="handlePlay"
      class="off"
      id="music"
      src="data:image/png;base64," 
      :width="80"
      :height="80"
      /> </div>
</template>

<script>
import { useSound } from '@vueuse/sound'
import buttonSfx from '../assets/14_MBH_teaser.mp3'

export default {
  setup() {
    const { play, stop} = useSound(buttonSfx);
    var isPlaying = new Boolean(true);
    const handlePlay = () => {
    if (isPlaying  )
    { 
       stop();
       isPlaying = false;
       document.getElementById("music").src = 'data:image/png;base64,';
       
    }
    else{ 
      play();
      isPlaying = true;
      document.getElementById("music").src = "data:image/png;base64,";
    };
  };

    return {
      handlePlay,
      play,
      stop,
    }
  },
}
</script>

<style>
#music {
  position : sticky;
  top: 18px;
}
</style>
